@charset 'utf-8';

@import '_settings';
@import 'reset';
@import 'prototype';
@import '_swiper.5.4.5';

$path: getPath();


.none {
  display: none;
}

.skipnav {
  position: absolute;
  top: -4rem;
  left: 0;
  width: 100%;
  text-align: center;
  background-color: #6D757C;
  padding: 1rem;
  z-index: 1000;
  transform: top .3s;

  &:focus {
    top: 0;
  }
}


#header {
  width: 100%;
  height: 7.2rem;
  background-color: #ffffff;

  .header-inner {
    display: flex;
    height: 100%;
    padding: 0 4.8rem;
    justify-content: space-between;
  }

  .logo,
  .header-gnb {
    display: inline-flex;
    align-items: center;
  }

  .title-h1 {
    display: flex;
    margin-left: -1rem;

    a {
      display: flex;
      width: 13.2rem;
      padding: 1rem;
      box-sizing: content-box;

      img {
        width: 100%;
      }
    }
  }

  .menu-box {
    display: flex;
    align-items: center;

    .menu-list {
      display: inline-flex;
      padding: 0 2rem;

      a {
        position: relative;
        display: inline-flex;
        padding: 1rem 0;
        align-items: center;
        font-weight: 600;
        text-transform: uppercase;

        &::after {
          position: absolute;
          left: 0;
          bottom: 1rem;
          content: "";
          width: 100%;
          height: .2rem;
          background-color: #0095F8;
          transform: scaleX(0);
          transition: transform .4s cubic-bezier(0.22, 0.61, 0.35, 1), background-color 0.3s;
          transform-origin: 100% 0;
        }

        &:hover {
          &::after {
            transform: scaleX(1);
            transform-origin: 0 0;
          }
        }
      }

      &.on {
        height: 4rem;
        padding-right: 0;

        a {
          padding: 0 3.2rem;
          color: #ffffff;
          border-radius: 20rem;
          background-color: #ed6c71;
          transition: background-color .3s ease;

          &::after {
            display: none;
          }

          &:hover {
            background-color: #0095F8;
          }
        }

      }
    }
  }
}

#footer {
  background-color: #23282C;

  .footer-inner {
    // width: 129.6rem;
    // margin: 0 auto;

    .contents {
      display: flex;
      width: 100%;
      justify-content: space-between;
      align-items: center;

      &.block {
        display: block;
      }

      &.top {
        border-bottom: 1px solid #2E3438;

        .contents-inner {
          padding: 1.6rem 0;
        }
      }

      .contents-inner {
        display: flex;
        justify-content: space-between;
        width: 129.6rem;
        margin: 0 auto;
      }

      &:nth-child(2) .contents-inner {
        display: flex;
        padding: 2.7rem 0 6.8rem;
      }
    }

    .footer-logo {
      display: inline-flex;
      margin-bottom: 0.8rem;

      img {
        width: 9.6em;
      }
    }

    .footer-content {
      display: inline-flex;

      .footer-content-list {
        display: inline-flex;
        align-items: center;

        a {
          display: inline-flex;
          align-items: center;
          padding: 1rem 2rem 1rem 0;
          color: #aaaaaa;
          font-weight: 600;

          &:hover {
            color: #ffffff;
          }

          &::before {
            display: inline-flex;
            content: "";
            width: 1px;
            height: 1.2rem;
            margin-right: 2rem;
            background: #40484F;
          }
        }

        &:first-child a::before {
          display: none;
        }
      }
    }

    .family-box-area {
      display: inline-flex;
      width: 24rem;

      .ui-select {
        position: relative;
        display: inline-flex;
        width: 100%;
        border: 1px solid #444444;
        border-radius: .8rem;
        background-color: #16191C;
        transition: border-color .3s ease;

        .btn-select {
          width: 100%;
          padding: .8rem 0 .8rem 2rem;
          color: #6D757C;
          font-weight: 600;
          text-align: left;
        }

        .select-area {
          .select-box {
            display: none;
            position: absolute;
            left: -1px;
            bottom: 4.2rem;
            width: 100%;
            height: 24rem;
            border: 1px solid #6D757C;
            border-bottom: 1px solid #40484F;
            border-radius: .8rem .8rem 0 0;
            background-color: #16191C;
            box-sizing: content-box;
            overflow-y: auto;

            .select-list {
              a {
                display: flex;
                padding: .8rem 2rem;
                color: #6D757C;
                font-weight: 600;

                &:hover {
                  color: #ffffff;
                }
              }
            }
          }

          &::after {
            position: absolute;
            top: 1.3rem;
            right: 2rem;
            display: flex;
            content: "";
            width: 1.6rem;
            height: 1.6rem;
            background: url(#{$path}/src/images/icons/ico_footer_arr.png) no-repeat 0 0;
            background-size: cover;
            transform: rotate(180deg);
          }
        }

        &.active {
          border: 1px solid #6D757C;
          border-radius: 0 0 .8rem .8rem;

          .select-area {
            .select-box {
              display: block;
            }

            &::after {
              transform: rotate(0);
            }
          }
        }
      }
    }

    .company-info {
      .contac {
        display: inline-flex;

        a {
          color: #6D757C;
          align-items: center;
        }
      }

      .mail {
        display: inline-flex;
        margin-left: 1.1rem;
      }
    }

    .address-1,
    .address-2 {
      margin-top: .6rem;

      ul {
        display: flex;

        li {
          display: inline-flex;
          padding-right: 1.2rem;
          color: #6D757C;
          align-items: center;

          &::before {
            display: inline-flex;
            content: "";
            width: 1px;
            height: 1.2rem;
            margin-right: 1rem;
            background: #40484F;
          }

          &:first-child {
            &::before {
              display: none;
            }
          }

          a {
            color: #6D757C;

            &:hover {
              color: #ffffff;
            }
          }
        }
      }
    }

    .social-area {
      display: flex;
      align-items: flex-end;

      ul {
        display: flex;
      }

      li {
        display: inline-flex;
        width: 2.8rem;
        height: 2.8rem;

        &+li {
          margin-left: 1.2rem;
        }

        a {
          display: inline-flex;
          width: 100%;
          height: 100%;

          &.ico-facebook {
            background: url(#{$path}/src/images/icons/ico_footer_facebook.png) no-repeat 0 0;
            background-size: cover;
          }

          &.ico-instagram {
            background: url(#{$path}/src/images/icons/ico_footer_instagram.png) no-repeat 0 0;
            background-size: cover;
          }

          &.ico-youtube {
            background: url(#{$path}/src/images/icons/ico_footer_youtube.png) no-repeat 0 0;
            background-size: cover;
          }

          &.ico-naver {
            background: url(#{$path}/src/images/icons/ico_footer_naver.png) no-repeat 0 0;
            background-size: cover;
          }

          &.ico-brunch {
            background: url(#{$path}/src/images/icons/ico_footer_brunch.png) no-repeat 0 0;
            background-size: cover;
          }
        }
      }
    }
  }
}


//quick menu
.quick-menu {
  position: fixed;
  top: 31.2rem;
  right: 5.1rem;
  z-index: 10;

  .aside-inner {
    display: flex;

    .aside-menu {
      background: #FFF;
      border: 1px solid #e1e8ed;
      border-radius: .8rem;
      box-shadow: 0px 4px 10px 0px rgba(0, 0, 0, 0.10);
      overflow: hidden;

      .menu-list {
        position: relative;
        display: flex;
        width: 10.8rem;
        justify-content: center;
        align-items: center;

        &.active {
          background-color: #ED6C71;

          a p.font-style-18 {
            color: #ffffff;
            font-weight: 600;
          }
        }

        a {
          display: flex;
          padding: 2.5rem 0;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          color: #444444;

          &::before {
            content: "";
            display: flex;
            width: 2.8rem;
            height: 2.8rem;
            margin-bottom: .6rem;
          }
        }

        &:nth-of-type(1) {
          a {
            &::before {
              background: url(#{$path}/src/images/icons/ico_quick_01.png) no-repeat 0 0;
              background-size: contain;
            }
          }
        }

        &:nth-of-type(2) {
          &::before {
            content: "";
            position: absolute;
            top: 0;
            left: 50%;
            display: flex;
            width: 9.5rem;
            height: 1px;
            background-color: #D9D9D9;
            transform: translateX(-50%);
          }

          a {
            &::before {
              background: url(#{$path}/src/images/icons/ico_quick_02.png) no-repeat 0 0;
              background-size: contain;
            }
          }
        }

        &:nth-of-type(3) {
          &::before {
            content: "";
            position: absolute;
            top: 0;
            left: 50%;
            display: flex;
            width: 9.5rem;
            height: 1px;
            background-color: #D9D9D9;
            transform: translateX(-50%);
          }

          a {
            &::before {
              background: url(#{$path}/src/images/icons/ico_quick_03.png) no-repeat 0 0;
              background-size: contain;
            }
          }
        }
      }
    }

    .btn-area {
      position: absolute;
      bottom: -11.3rem;
      left: 50%;
      width: 9rem;
      height: 9rem;
      transform: translateX(-50%);

      .btn-top {
        display: flex;
        width: 100%;
        height: 100%;
        justify-content: center;
        align-items: center;
        border-radius: 50%;
        background-color: #ffffff;
        fill: #ffffff;
        stroke-width: 1px;
        stroke: #D9D9D9;
        filter: drop-shadow(0px 4px 20px rgba(0, 0, 0, 0.08));

        &::after {
          display: flex;
          content: "";
          width: 2.4rem;
          height: 2.4rem;
          background: url(#{$path}/src/images/icons/ico_arr_gray.png) no-repeat 0 0;
          background-size: contain;
        }
      }
    }
  }
}

.visual-top {
  width: 100%;
  height: 48.4rem;
  background-color: lightpink;

  &::after {
    content: "Visual Top";
    display: flex;
    width: 100%;
    height: 100%;
    font-size: 3.2rem;
    justify-content: center;
    align-items: center;
  }
}

