$path: getPath();

.prototype-container {
  padding: 3rem;
}
.partition {
  padding: 1.5rem 0;
  // border-top: 1px solid #444444;
  border-bottom: 1px solid #e4e4e4;
}
.subsrc {
  display: block;
  font-size: 3.6rem;
  font-weight: 700;
  line-height: 100%;
  margin: 1.5rem 0;
  color: lightslategray;

  .caption {
    display: block;
    margin-top: 0.8rem;
    font-size: 1.8rem;
    font-style: normal;
    line-height: 100%;
    color: #444444;
  }
}

.explain {
  font-size: 1.6rem;
  font-weight: 400;
  line-height: 100%;
  margin: 1.5rem 0;
}

// menu
.prototype-menu {
  top: 5.8rem;
}
.prototype-list {
  top: 11.5rem;
}
.prototype-menu,
.prototype-list {
  position: sticky;
  border-bottom: 1px solid #000000;
  background-color: #ffffff;
  z-index: 1;

  ul {
    display: flex;
    padding: 0 2rem;

    li {
      position: relative;
      padding: 1rem 0;
      display: inline-flex;
      justify-content: center;
      align-items: center;

      a {
        padding: 0.5rem;
        border: 1px solid transparent;
        font-size: 1.4rem;
      }

      & + li {
        margin-left: 2rem;
      }
    }

    li.active {
      a {
        border: 1px solid #000000;
        border-radius: 0.5rem;
      }
    }
  }
}

.prototype-list {
  ul {
    padding: 0 3rem;
  }
}

//타이틀
.title-h1 {
