//input
.ui-input {
  display: inline-flex;

  input[type="checkbox"] {
    display: none;

    &:checked + label::before {
      content: "✓";
      color: #ffffff;
      font-size: 1.4rem;
      justify-content: center;
      align-items: center;
      background-color: #0095f8;
    }

    + label {
      display: inline-flex;
      justify-content: center;
      align-items: center;

      &::before {
        display: inline-flex;
        content: "";
        width: 2rem;
        height: 2rem;
        font-size: 1.4rem;
        background-color: #f1f4f8;
        border-radius: 50%;
        cursor: pointer;
        box-sizing: content-box;
      }
    }
  }

  label {
    &::before {
      margin-right: 1rem;
    }
  }

  &.no-label {
    input[type="checkbox"] {
      display: none;

      &:checked + label {
        background-color: #0095f8;

        &::before {
          position: absolute;
          top: 0;
          left: 0;
          display: inline-flex;
          content: "✓";
          color: #ffffff;
          width: 100%;
          height: 100%;
          font-size: 1.4rem;
          justify-content: center;
          align-items: center;
        }
      }
    }

    label {
      position: relative;
      display: inline-flex;
      width: 2rem;
      height: 2rem;
      border-radius: 50%;
      background-color: #f1f4f8;
      cursor: pointer;

      &::before {
        display: none;
        margin-right: 0;
      }
    }
  }

  &.label-right {
    label {
      display: inline-flex;
      flex-direction: row-reverse;

      &::before {
        margin-left: 1rem;
      }
    }
  }

  &.type-square {
    input[type="checkbox"] {
      &:checked + label {
        border-radius: 0.2rem;

        &::before {
          border-radius: 0.2rem;
        }
      }
    }

    label {
      &::before {
        border-radius: 0.2rem;
      }

      border-radius: 0.2rem;
    }
  }
}

//section
.section {
  display: flex;
  width: 100%;
  padding:18rem 0;
}


@media (max-width:1023px) {
  @import './prototype_tablet';
}

@media (max-width:767px) {
